                l.delta[box_index+3] = l.coord_scale*(sqrt(state.truth[tbox_index + 3]) - l.output[box_index + 3]);
            }

            *(l.cost) += pow(1-iou, 2);
            avg_iou += iou;
            ++count;
        }
    }

