// l: data pointer to the list
// key: the key to find, example: "filters", "padding"
// def: default value
int option_find_int(list *l, char *key, int def)
{
    // keyatoi
    char *v = option_find(l, key);
    if(v) return atoi(v);
    // XXX_quiet
    fprintf(stderr, "%s: Using default '%d'\n", key, def);
    // key
    return def;
}
