``` cpp
/**
 * @brief Manages memory allocation and synchronization between the host (CPU)
 *        and device (GPU).
 *
 * TODO(dox): more thorough description.
 */
class SyncedMemory {
 public:
  SyncedMemory();
  explicit SyncedMemory(size_t size);
  ~SyncedMemory();
  // CPU data
  const void* cpu_data();
  // CPU data
  void set_cpu_data(void* data);
  // GPU data
  const void* gpu_data();
  // GPU data
  void set_gpu_data(void* data);
  // CPU data
  void* mutable_cpu_data();
  // GPU data
  void* mutable_gpu_data();
  // CPU  GPUCPUGPU
  enum SyncedHead { UNINITIALIZED, HEAD_AT_CPU, HEAD_AT_GPU, SYNCED };
  SyncedHead head() { return head_; }
  // 
  size_t size() { return size_; }

#ifndef CPU_ONLY
  void async_gpu_push(const cudaStream_t& stream);
#endif

 private:
  void check_device();

  void to_cpu();
  void to_gpu();
  void* cpu_ptr_;
  void* gpu_ptr_;
  size_t size_;
  SyncedHead head_;
  bool own_cpu_data_;
  bool cpu_malloc_use_cuda_;
  bool own_gpu_data_;
  // GPU
  int device_;

  DISABLE_COPY_AND_ASSIGN(SyncedMemory);
};  // class SyncedMemory
```

`to_cpu()`

``` cpp
inline void SyncedMemory::to_gpu() {
  // DEBUG
  check_device();
#ifndef CPU_ONLY
  switch (head_) {
  case UNINITIALIZED:
    // ~
    // GPU~
    CUDA_CHECK(cudaMalloc(&gpu_ptr_, size_));
    caffe_gpu_memset(size_, 0, gpu_ptr_);
    // 
    head_ = HEAD_AT_GPU;
    own_gpu_data_ = true;
    break;
  case HEAD_AT_CPU:
    // CPU~
    if (gpu_ptr_ == NULL) {
      CUDA_CHECK(cudaMalloc(&gpu_ptr_, size_));
      own_gpu_data_ = true;
    }
    // 
    caffe_gpu_memcpy(size_, cpu_ptr_, gpu_ptr_);
    // 
    head_ = SYNCED;
    break;
  // GPU
  case HEAD_AT_GPU:
  case SYNCED:
    break;
  }
#else
  // NO_GPU FATAL ERROR
  // GPU 
  NO_GPU;
#endif
}
```

